import React, {useState} from 'react';
import Expense from "./ExpenseItem";
import Card from "../UI/Card";
import './Expenses.css'
import ExpensesFilter from "../NewExpense/ExpenseFilter";

const Expenses = (props) => {

  const [filteredYear, setfilteredYear] = useState('2020');
  
  const onChangeFilterHandler = (selectedYear) => {
    setfilteredYear(selectedYear);
  };

  return (
    <Card className = 'expenses'>
      <ExpensesFilter selected ={filteredYear} onChangeFilter = {onChangeFilterHandler}></ExpensesFilter>
      
      {props.expenses.map( (expense) => {<Expense title={expense.title} amount={expense.amount}
        date={expense.date}></Expense>})}
    
    </Card>
  );
};

export default Expenses;
