import React, {useState} from "react";
import './ExpenseForm.css'

const ExpenseForm = () =>{

    // const [EnteredTitle, setEnteredTitle] = useState('');
    // const [EnteredAmount, setEnteredAmount] = useState('0.0');
    // const [EnteredDate, setEnteredDate] = useState('');

    const [userInput, setUserInput] = useState({
        EnteredTitle:'',
        EnteredAmount:'',
        EnteredDate:''
    });

    const onTitleChangeHandler = (event) => {
        setUserInput({
            ...userInput,
            EnteredTitle : event.target.value,
        });
    }

    const onAmountChangeHandler = (event) =>{
        setEnteredAmount(event.target.value);
    }

    const onDateChangeHandler = (event) =>{
        setEnteredDate(event.target.value);
    }

    return (
        <form>
            <div className="new-expense__control">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type = "text" onChange={onTitleChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type = "number" min = "0.01" step ="0.01" onChange={onAmountChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type = "date" min = "2019-01-01" max= "2024-01-01" onChange={onDateChangeHandler}></input>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </form>
    );
}

export default ExpenseForm;
